mapper.select:
SELECT i_user, user_id, user_pw, salt, nm, user_type, bd, profile_img, r_dt, m_dt
FROM t_user
WHERE  i_user = #{i_user}
user_id = #{user_id}

mapper.update:
UPDATE t_user
SET
nm = #{nm}

,user_pw = #{user_pw}
,salt = #{salt}


,profile_img = #{profile_img}

WHERE i_user = #{i_user}
